<?php

namespace Buggl\MainBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query\ResultSetMappingBuilder;

/**
 * SpotLikeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SpotLikeRepository extends EntityRepository
{
	public function findBySpotType(\Buggl\MainBundle\Entity\SpotType $spotType, $author = null)
	{
		$sql = "SELECT sl.* FROM spot_like as sl WHERE 1 AND sl.spot_type_id = ? AND sl.status = 1";
		if(!is_null($author))
		{
			$sql .= " UNION ALL SELECT sl.* FROM spot_like as sl WHERE 1 AND sl.spot_type_id = ? AND sl.local_author_id = ?";

			$params = array($spotType->getID(), $spotType->getID(), $author->getID());
		}
		else {
			$params = array($spotType->getID());
		}
		
		$em = $this->getEntityManager();
		$rsm = new ResultSetMappingBuilder($em);
		$rsm->addRootEntityFromClassMetadata('BugglMainBundle:SpotLike', 'sl');
		$query = $this->_em->createNativeQuery($sql, $rsm);
		$query->setParameters($params);
		try {
			$result = $query->getResult();
		} catch(\Doctrine\ORM\NoResultException $e){
			return null;
		}
		return $result;
	}
}
